def countHandleWithNumber(handle)#Méthode qui va compter le nombre de pseudo avec un nombre/chiffre
    nbHandle = 0
    handle.each do |user|#On séléctionne chaque pseudo
      number = 0
      user.split("").each do |char|#On séléctionne chaque caractère de chaque pseudo
        if char >= "0" and char <= "9"#Si le caractère du pseudo contient un nombre sup ou égale à 0 ou inf ou égale à 9
          number = 1
        end
      end
      if number == 1
        nbHandle += 1
      end
    end
    return nbHandle
end

def audeHandle(handle)#Méthode qui va compter le nombre de pseudo avec qui contiennent les lettre "aude"
    nbAudeHandle = 0
    handle.each do |user|
        if (user.downcase).include?("aude")#Si l'élément en minuscule inclue les lettres "aude" incrémente  nbAudeHandle
            nbAudeHandle +=1
        end
    end 
    return nbAudeHandle
end

def upcaseHandle(handle)#Méthode qui va compter le nb de pseudo avec la première lettre en majuscule 
    nbupcaseHandle = 0
    handle.each do |user| #Chaque élément de l'array "handle" ici appelé user
        if user[1] == user[1].upcase#Si le deuxième caratère de l'élément est en majuscule 
            nbupcaseHandle += 1#On incrémente le nombre total de pseudo avec une majuscule
        end
    end 
    return nbupcaseHandle#On affecte la valeur de nbupcaseHandle à la méthode
end

def handleWithUpcase(handle)#Méthode qui va compter le nb de pseudo avec une lettre majuscule à l'intérieur 
  nbHandleUpcase = 0
  handle.each do |user|
    number = 0
    count = 0
    user.split("").each do |char|#Check chaque caractère de chaque élément de l'array 
      if count != 0
        if char == char.upcase && ((char >= "A" && char <= "Z") || (char >= "a" && char <= "z"))#Si le char est une majuscule entre A et Z ou a et z
          number = 1
        end
      end
      count += 1
    end
    if number == 1
      nbHandleUpcase += 1
    end
  end
  return nbHandleUpcase
end

def underscoreInHandle(handle)#Méthode qui va compter le nb de pseudo avec un underscore à l'intérieur 
  nbUnderscore = 0# Le nombre d'élément avec un "_"
  handle.each do |user|#Chaque élément de l'array "handle" ici appelé user
    user.split("").each do |char|
      if char == "_"
        nbUnderscore += 1
      end
    end
  end
  return nbUnderscore#On affecte le la valeur de nbUnderlineHandle à la méthode underlineHandle
end

